<style lang="postcss">
  .cover-art {
    @apply bg-purple-light bg-photocopy shadow ml-3 hidden lg:block scale-50 p-12 rounded-lg overflow-hidden;
    background-image: url("../src/assets/pattern.png");
    user-select: none;
    width: 23vw;
    animation: slide 1s steps(3) infinite;
    height: 23vw;
  }
  .musical-notes {
    @apply font-icon text-5xl;
    position: relative;
    bottom: 48vw;
    left: 20vw;
    p {
      @apply my-0;
      transform: rotate(-2deg);
    }
    .note-music-1 {
      position: relative;
      right: 5vw;
      top: 6vw;
      transform: rotate(30deg);
    }
  }
  @keyframes slide {
    from {
      background-position: 0%;
    }

    to {
      background-position: 150%;
    }
  }
  .bobby-album {
    position: relative;
    bottom: 36.5vw;
    width: 7vw;
    left: 11.5vw;
  }
  .dashes-album {
    position: relative;
    width: 23vw;
    height: 23vw;
    position: relative;
    display: flex;
    @apply transform scale-125 2xl:scale-110;
    left: 0.05em;
    rect {
      animation: 2s loopDash linear infinite;
    }
  }
  .logo {
    width: 20vw;
    position: relative;
    bottom: 17vw;
    left: 0.3em;
    .rainbow-mask {
      background: linear-gradient(
          272deg,
          rgb(255, 229, 0),
          rgb(242, 139, 39),
          rgb(196, 41, 169),
          rgb(68, 69, 255),
          rgb(11, 241, 49),
          rgb(255, 229, 0)
        )
        repeat scroll 0% 0% / 200% 200%;
      animation: 5s linear 0s infinite normal none running logoGrad;
      mask: url("data:image/svg+xml;base64,");
      height: 14.34vw;
      width: 20vw;
      position: absolute;
      z-index: 0;
    }
    #white-layer {
      z-index: 1;
      left: 0.35rem;
      position: relative;
    }
    .album-title {
      width: 25vw;
      @apply text-lg xl:text-xl 2xl:text-3xl my-0;
    }
  }
  @keyframes logoGrad {
    0% {
      background-position: 0%;
    }
    100% {
      background-position: 200%;
    }
  }
  @keyframes loopDash {
    0% {
      stroke-dashoffset: 0;
    }
    100% {
      stroke-dashoffset: 115;
    }
  }
</style>

